pragma solidity >=0.4.21 <0.7.0;

import "./Ownable.sol";

contract CryptoTraderHelper is Ownable {
    mapping (address => mapping (string => uint)) traderToCurrencyToBalance;
    mapping (address => uint[]) traderToCompetitions;

    string virtualCurrency = "TraderCoin";
    string realCurrency = "Ethereum";
    uint currentCompetition = 1;
    uint startTimestamp = now;
    uint competitionDuration = 7 days;

    modifier isOpen(uint _competitionId) {
        require(now < startTimestamp + competitionDuration, "Competition is closed.");
        require(_competitionId == currentCompetition, "Not the current competition.");
        _;
    }

    modifier isFutureCompetition(uint _competitionId) {
        require(_competitionId > currentCompetition, "The competition is over or is running.");
        _;
    }

    modifier isParticipant(address _trader, uint _competitionId) {
        require(_participate(_trader, _competitionId), "Trader is not in the competition.");
        _;
    }

    modifier isNotParticipant(address _trader, uint _competitionId) {
        require(!_participate(_trader, _competitionId), "Trader is already in the competition.");
        _;
    }

    modifier hasSufficientBalance(bool _buy, address _trader, uint _amount) {
        if (true == _buy) {
            require(traderToCurrencyToBalance[_trader][virtualCurrency] >= _amount * getPrice(), "Not sufficient virtual currency balance to buy.");
        } else {
            require(traderToCurrencyToBalance[_trader][realCurrency] >= _amount, "Not sufficient real currency balance to sell.");
        }
        _;
    }

    // Return true if the trader (param1) is in the competition (param2)
    function _participate(address _trader, uint _competitionId) private view returns (bool) {
        uint[] memory competitions = traderToCompetitions[_trader];
        bool participant = false;

        for (uint i = 0; i < competitions.length; i++) {
            if (competitions[i] == _competitionId) {
                participant = true;
                break;
            }
        }

        return participant;
    }

    function setCompetitionDuration(uint _competitionDuration) external onlyOwner {
        competitionDuration = _competitionDuration;
    }

    function getPrice() public view returns (uint) {
        // Implement price feeder method
        return 200;
    }

    // manque la logique pour switcher de compétition
    // pour reset les compteurs
    // pour désigner un gagnant
}
